mapper.select:
SELECT b.post_no, c.category_name, c.subcategory, b.title, m.name, b.author, CASE WHEN r.re > 0 THEN '답변완료' ELSE '답변전' END status FROM board b INNER JOIN board_categories c ON b.category_id = c.category_id INNER JOIN member m ON b.author = m.member_id LEFT JOIN board_uf f ON b.post_no = f.post_no LEFT JOIN (select s.post_no, s.re, s.ref, s.ref_order from (select b.post_no, b.ref, b.ref_order, (select count(*) from board where ref = b.ref and step = b.step + 1 and ref_order >= b.ref_order + 1) re from board b) s) r ON b.post_no = r.post_no
AND created_date BETWEEN #{createdDateStart} AND #{createdDateEnd}
AND b.category_id = #{categoryId}
AND b.title = #{title}
AND b.content = #{content}
AND b.author = #{author}
AND r.re > 0
AND r.re = 0
f.upload_files_seq IS NOT NULL and f.upload_files_seq != ''
f.upload_files_seq IS NULL or f.upload_files_seq = ''
ORDER BY r.ref DESC, r.ref_order ASC;
