mapper.select:
SELECT s.no, s.created_date, s.admin_id, s.important, s.content
FROM (SELECT @ROWN:=@ROWN+1 no, 
			 m.created_date, 
			 m.admin_id, 
			 m.important, 
			 m.content
		FROM admin_memo m

			(@ROWN:=0) = 0 AND m.member_id = #{memberId} 

         		AND m.created_date BETWEEN #{createdDateStart} AND #{createdDateEnd}


				AND m.important = #{important}


				AND m.content LIKE CONCAT('%',#{content},'%')


		ORDER BY m.created_date ASC) s
ORDER BY s.no DESC;
