mapper.update:
UPDATE order_history h LEFT JOIN ( SELECT count(detailed_order_id) count, order_no FROM detailed_order_items WHERE product_status = '입금전' AND order_no IN
#{orderNo}
) d ON h.order_no = d.order_no SET h.delivery_status = if(COALESCE(d.count, 0) = 0 ,'주문취소', '부분취소') WHERE h.order_no IN
#{orderNo}

