#{orderNo}
) c ON d.order_no = c.order_no WHERE d.order_no IN
#{orderNo}

mapper.update:
INSERT INTO reward_logs (member_id, reason, reward, related_order, balance) SELECT m.member_id, '주문취소' reason, p.used_reward reward, h.order_no related_order, (m.total_rewards + p.used_reward) balance FROM payments p INNER JOIN order_history h ON p.order_no = h.order_no INNER JOIN member m ON h.member_id = m.member_id INNER JOIN reward_logs r ON h.order_no = r.related_order WHERE p.payment_status = '결제완료' AND r.related_order IN
#{orderNo}
AND EXISTS (SELECT related_order FROM reward_logs WHERE related_order = h.order_no AND reason = "구매")

INSERT INTO point_logs (member_id, reason, point, related_order, balance) SELECT m.member_id, '주문취소' reason, p.used_points point, h.order_no related_order, (m.total_points + p.used_points) balance FROM payments p INNER JOIN order_history h ON p.order_no = h.order_no INNER JOIN member m ON h.member_id = m.member_id INNER JOIN point_logs l ON h.order_no = l.related_order WHERE p.payment_status = '결제완료' AND l.related_order IN
#{orderNo}
AND EXISTS (SELECT related_order FROM point_logs WHERE related_order = h.order_no AND reason = "구매")

