	         		AND confirm_date BETWEEN #{confirmDateStart} AND #{confirmDateEnd}


		ORDER BY h.order_time DESC, d.product_order_no DESC

		SELECT 	 	h.order_time,
			     	h.order_no,
		CASE   WHEN product_count.count = 1  THEN product_count.product_name
			   ELSE CONCAT(product_count.product_name, ' 외 ', product_count.count - 1, '건')
		        END product_name,  
		         	m.name,
		         	h.member_id,
                 	b.payer_name,
                 	b.deposit_amount,
                 	b.bank_name,
                	if(b.deposit_amount != 0 AND b.deposit_amount is not null, '결제완료', '입금전') status,
		         	h.delivery_message
  		FROM 	    payments p
		INNER JOIN  order_history h 
		ON 			p.order_no = h.order_no
		INNER JOIN  member m 
		ON 			h.member_id = m.member_id 
		LEFT JOIN   bank_transfers b 
		ON 			p.payment_number = b.payment_number
		LEFT JOIN   (
						SELECT  	d.order_no, p.product_name, p.category_key, count(distinct d.product_id) count
						FROM 		detailed_order_items d
						INNER JOIN  products p
						ON 			d.product_id = p.product_id
						GROUP BY 	d.order_no
		            ) product_count
		ON 			h.order_no = product_count.order_no

			p.payment_method = 'bkt' 
				AND (b.deposit_amount = 0 OR b.deposit_amount is null)
				AND h.delivery_status = '입금전'

				AND h.order_no = #{orderNo}


				AND d.product_order_no = #{productOrderNo}


				AND h.invoice_number = #{invoiceNumber}


				AND m.name = #{name}


				AND h.member_id = #{memberId}


				AND m.email = #{email}


				AND m.cell_phone_number = #{cellPhoneNumber}


				AND m.phone_number = #{phoneNumber}


				AND b.payer_name = #{payerName}


				AND h.recipient_name = #{recipientName}


				AND h.recipient_phone_number = #{recipientPhoneNumber}


				AND h.shipping_address = #{shippingAddress}


				AND product_count.product_name like CONCAT('%',#{productName},'%')                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     NCAT('%',#{productName},'%') 


				AND d.product_id = #{productId}


				AND product_count.category_key = #{categoryKey}


	         		AND order_time BETWEEN #{orderTimeStart} AND #{orderTimeEnd}


	         		AND confirm_date BETWEEN #{confirmDateStart} AND #{confirmDateEnd}


		ORDER BY h.order_time DESC

	SELECT 	 	h.order_time,
			 	p.payment_time,
			 	p.order_no,
	        	d.product_order_no,
	         	m.name,
	         	h.member_id,
	         	pr.product_name,
	         	pr.product_id,
	         	d.product_quantity,
	         	d.product_price,
	        	p.actual_payment_amount,
	         	p.payment_method,
	         	p.payment_status,
	         	d.product_status,
	         	d.product_invoice_number,
	         	h.delivery_message
	FROM 		payments p
	INNER JOIN  order_history h 
	ON 			p.order_no = h.order_no
	INNER JOIN  member m 
	ON 			h.member_id = m.member_id 
	INNER JOIN  detailed_order_items d
	ON 			p.order_no = d.order_no
	INNER JOIN  products pr
	ON 			d.product_id = pr.product_id


			AND h.order_no = #{orderNo}


			AND d.product_order_no = #{productOrderNo}


			AND h.invoice_number = #{invoiceNumber}


			AND m.name = #{name}


			AND h.member_id = #{memberId}


			AND m.email = #{email}


			AND m.cell_phone_number = #{cellPhoneNumber}


			AND m.phone_number = #{phoneNumber}


			AND b.payer_name = #{payerName}


			AND h.recipient_name = #{recipientName}


			AND h.recipient_phone_number = #{recipientPhoneNumber}


			AND h.shipping_address = #{shippingAddress}


			AND product_count.product_name like CONCAT('%',#{productName},'%')                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                     NCAT('%',#{productName},'%') 


			AND d.product_id = #{productId}


			AND product_count.category_key = #{categoryKey}


         		AND h.order_time BETWEEN #{orderTimeStart} AND #{orderTimeEnd}


         		AND p.payment_time BETWEEN #{paymentTimeStart} AND #{paymentTimeEnd}


	ORDER BY h.order_time DESC, d.product_order_no DESC

SELECT h.order_time, p.payment_time, h.order_no, m.name, h.member_id, CASE WHEN product_count.count = 1 THEN product_count.product_name ELSE CONCAT(product_count.product_name, ' 외 ', product_count.count - 1, '건') END product_name, p.total_amount, p.actual_payment_amount, p.payment_method, p.payment_status, status.undelivered, status.inTransit, status.delivered, status.cancels, status.exchanges, status.returns, h.delivery_message FROM payments p INNER JOIN order_history h ON p.order_no = h.order_no INNER JOIN member m ON h.member_id = m.member_id LEFT JOIN bank_transfers b ON p.payment_number = b.payment_number LEFT JOIN ( SELECT d.order_no, (SELECT count(*) FROM detailed_order_items WHERE product_status in ('입금전','결제완료', '출고완료','주문취소','부분취소') and order_no = d.order_no) 'Undelivered', (SELECT count(*) FROM detailed_order_items WHERE product_status = '배송중' and order_no = d.order_no) 'intransit', (SELECT count(*) FROM detailed_order_items WHERE product_status in ('배송완료','반품','교환','환불') and order_no = d.order_no) 'Delivered', (SELECT count(*) FROM detailed_order_items WHERE product_status = '주문취소' or product_status = '부분취소' and order_no = d.order_no) 'cancels', (SELECT count(*) FROM detailed_order_items WHERE product_status = '교환' and order_no = d.order_no) 'exchanges', (SELECT count(*) FROM detailed_order_items WHERE product_status = '반품' and order_no = d.order_no) 'returns' FROM detailed_order_items d GROUP BY d.order_no ) status ON h.order_no = status.order_no LEFT JOIN ( SELECT d.order_no, p.product_name, p.category_key, count(distinct d.product_id) count FROM detailed_order_items d INNER JOIN products p ON d.product_id = p.product_id GROUP BY d.order_no ) product_count ON h.order_no = product_count.order_no
AND h.order_no = #{orderNo}
AND d.product_order_no = #{productOrderNo}
AND h.invoice_number = #{invoiceNumber}
AND m.name = #{name}
AND h.member_id = #{memberId}
AND m.email = #{email}
AND m.cell_phone_number = #{cellPhoneNumber}
AND m.phone_number = #{phoneNumber}
AND b.payer_name = #{payerName}
AND h.recipient_name = #{recipientName}
AND h.recipient_phone_number = #{recipientPhoneNumber}
AND h.shipping_address = #{shippingAddress}
AND product_count.product_name like CONCAT('%',#{productName},'%') NCAT('%',#{productName},'%')
AND d.product_id = #{productId}
AND product_count.category_key = #{categoryKey}
AND h.order_time BETWEEN #{orderTimeStart} AND #{orderTimeEnd}
AND p.payment_time BETWEEN #{paymentTimeStart} AND #{paymentTimeEnd}
ORDER BY h.order_time DESC
